const BinWrapper = require('bin-wrapper')
const binVersionCheck = require('bin-version-check')
const fs = require('fs')
const path = require('path')
const childProcess = require('child_process')

const binary = require('./pandoc.json')

const pandoc = new BinWrapper()
  // Semver requirement for Pandoc
  .version(binary.version)
  // Sources for downloaded binaries (if necessary)
  .src(binary.linux, 'linux', 'x64')
  .src(binary.macos, 'darwin')
  .src(binary.windows, 'win32')
  .dest((function () {
    // Destination for downloaded binaries (if necessary)
    switch (process.platform) {
      case 'darwin':
        return path.join(process.env.HOME, 'Library', 'Application Support', 'Stencila')
      case 'linux':
        return path.join(process.env.HOME, '.stencila')
      case 'win32':
        return path.join(process.env.APPDATA, 'Stencila')
      default:
        return path.join(process.env.HOME, 'stencila')
    }
  }()))
  .use(process.platform === 'win32' ? 'pandoc.exe' : 'bin/pandoc')

let ready = false

pandoc.get = function () {
  return new Promise((resolve, reject) => {
    if (ready) resolve()
    else {
      // Check that an acceptable version of Pandoc is available on PATH
      binVersionCheck('pandoc', pandoc.version()).then(() => {
        // Use global Pandoc by setting `pandoc.path()` to 'pandoc'
        // console.info(' Global Pandoc is OK')
        pandoc.dest('')
        pandoc.use('pandoc')
        ready = true
        resolve()
      }).catch(() => {
        // Global pandoc is not available/acceptable, check local Pandoc
        binVersionCheck(pandoc.path(), pandoc.version()).then(() => {
          // console.info(' Local Pandoc is OK: ' + pandoc.path())
          ready = true
          resolve()
        }).catch(() => {
          console.info(' About to download Pandoc ' + binary.version + ' to: ' + pandoc.path())
          fs.unlink(pandoc.path(), function () {
            console.log(' Downloading Pandoc (this could take some time).')
            pandoc.run(['--version'], function (err) {
              if (err) {
                console.log(' Download fail :(')
                reject(new Error())
              } else {
                console.log(' Download success!')
                ready = true
                resolve()
              }
            })
          })
        })
      })
    }
  })
}

pandoc.spawn = function (input, args) {
  return pandoc.get().then(() => {
    return new Promise((resolve, reject) => {
      const child = childProcess.spawn(pandoc.path(), args)
      let stdout = ''
      child.stdout.on('data', (data) => {
        stdout += data
      })
      child.stdout.on('end', () => {
        resolve(stdout)
      })
      child.stderr.on('data', (data) => {
        reject(new Error(data))
      })
      child.on('error', (err) => {
        reject(err)
      })
      child.stdin.setEncoding('utf-8')
      child.stdin.write(input)
      child.stdin.end()
    })
  })
}

module.exports = pandoc

const projectConverters = require('./project')
const documentConverters = require('./document')
const sheetConverters = require('./sheet')

let converters = [].concat(
  Object.values(projectConverters),
  Object.values(documentConverters),
  Object.values(sheetConverters)
)

function import_ (pathFrom, pathTo, volumeFrom, volumeTo) {
  return Promise.resolve().then(() => {
    function check (index = 0) {
      const Converter = converters[index]
      if (!Converter) throw new Error('No converters can import from ' + pathFrom)
      let converter = new Converter()
      return converter.canImport(pathFrom, volumeFrom).then((can) => {
        return can ? converter.import(pathFrom, pathTo, volumeFrom, volumeTo) : check(index + 1)
      })
    }
    return check()
  })
}

function export_ (pathFrom, pathTo, volumeFrom, volumeTo) {
  return Promise.resolve().then(() => {
    function check (index = 0) {
      const Converter = converters[index]
      if (!Converter) throw new Error('No converters can export from ' + pathFrom)
      let converter = new Converter()
      return converter.canExport(pathTo, volumeTo).then((can) => {
        return can ? converter.export(pathFrom, pathTo, volumeFrom, volumeTo) : check(index + 1)
      })
    }
    return check()
  })
}

function convert (pathFrom, pathTo, volumeFrom, volumeTo) {
  return import_(pathFrom, null, volumeFrom, volumeTo).then((pathImportedTo, volumeImportedTo) => {
    return export_(pathImportedTo, pathTo, volumeImportedTo, volumeTo)
  })
}

module.exports = {
  'import': import_,
  'export': export_,
  convert
}

